import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { RotateCcw, Lightbulb } from 'lucide-react';
import './Flashcard.css';

const Flashcard = ({ card, showHint, onShowHint }) => {
  const [isFlipped, setIsFlipped] = useState(false);
  const [showHintLocal, setShowHintLocal] = useState(false);

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  const handleShowHint = () => {
    if (!showHint) {
      setShowHintLocal(!showHintLocal);
      onShowHint?.();
    }
  };

  const currentHint = showHint || showHintLocal;

  // Debug: Log the card data
  console.log('Flashcard data:', card);
  console.log('Card question:', card?.question);
  console.log('Card answer:', card?.answer);

  // If no card data, show a placeholder
  if (!card) {
    return (
      <div className="flashcard-wrapper">
        <div className="flashcard-placeholder">
          <h3>No card data available</h3>
          <p>Please check if the topic has any flashcards.</p>
        </div>
      </div>
    );
  }

  // Very simple test version
  return (
    <div className="flashcard-wrapper">
      <div style={{
        width: '100%',
        maxWidth: '500px',
        height: '400px',
        backgroundColor: '#2a2a2a',
        border: '2px solid #404040',
        borderRadius: '12px',
        padding: '2rem',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        color: 'white'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
          <div style={{ fontSize: '2rem', color: '#6366f1' }}>â™ </div>
          <div style={{ 
            fontSize: '1.5rem', 
            fontWeight: '700', 
            color: 'white', 
            backgroundColor: '#6366f1',
            width: '32px',
            height: '32px',
            borderRadius: '50%',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>Q</div>
        </div>

        <div style={{ 
          flex: 1, 
          display: 'flex', 
          flexDirection: 'column', 
          justifyContent: 'center', 
          alignItems: 'center', 
          textAlign: 'center',
          gap: '1.5rem'
        }}>
          <h3 style={{ 
            fontSize: '1.5rem', 
            fontWeight: '600', 
            color: 'white', 
            margin: 0,
            lineHeight: 1.4
          }}>
            {card.question || 'No question available'}
          </h3>

          {card.hint && (
            <button
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.5rem',
                backgroundColor: '#f59e0b',
                color: 'white',
                padding: '0.75rem 1.25rem',
                borderRadius: '8px',
                fontWeight: '500',
                fontSize: '0.9rem',
                border: 'none',
                cursor: 'pointer'
              }}
              onClick={handleShowHint}
            >
              <Lightbulb size={16} />
              Show Hint
            </button>
          )}

          {currentHint && card.hint && (
            <div style={{
              backgroundColor: 'rgba(245, 158, 11, 0.2)',
              border: '1px solid #f59e0b',
              borderRadius: '8px',
              padding: '1rem',
              display: 'flex',
              alignItems: 'center',
              gap: '0.75rem',
              maxWidth: '100%'
            }}>
              <div style={{ fontSize: '1.25rem' }}>ðŸ’¡</div>
              <p style={{ 
                color: 'white', 
                margin: 0, 
                fontSize: '0.95rem', 
                lineHeight: 1.4, 
                textAlign: 'left' 
              }}>{card.hint}</p>
            </div>
          )}
        </div>

        <div style={{ display: 'flex', justifyContent: 'center' }}>
          <button
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              backgroundColor: '#6366f1',
              color: 'white',
              padding: '0.75rem 1.5rem',
              borderRadius: '8px',
              fontWeight: '500',
              fontSize: '0.95rem',
              border: 'none',
              cursor: 'pointer'
            }}
            onClick={handleFlip}
          >
            <RotateCcw size={18} />
            Flip Card
          </button>
        </div>
      </div>
    </div>
  );
};

export default Flashcard;
