import React, { useState, useEffect } from 'react';
import TopicPanel from './components/TopicPanel';
import FlashcardDeck from './components/FlashcardDeck';
import './App.css';

function App() {
  const [topics, setTopics] = useState([]);
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [isDeckView, setIsDeckView] = useState(false);

  // Load topics from localStorage on component mount
  useEffect(() => {
    const savedTopics = localStorage.getItem('flashcard-topics');
    if (savedTopics) {
      setTopics(JSON.parse(savedTopics));
    } else {
      // Initialize with some sample topics
      const sampleTopics = [
        {
          id: '1',
          name: 'JavaScript Basics',
          description: 'Fundamental concepts of JavaScript programming',
          cards: [
            {
              id: '1-1',
              question: 'What is a variable in JavaScript?',
              hint: 'Think about storing data...',
              answer: 'A variable is a container for storing data values. It can hold different types of data like numbers, strings, objects, etc. Variables are declared using keywords like `let`, `const`, or `var`.'
            },
            {
              id: '1-2',
              question: 'What is the difference between `let` and `const`?',
              hint: 'One can be reassigned, the other cannot...',
              answer: '`let` allows you to reassign values to the variable, while `const` creates a read-only reference that cannot be reassigned. However, `const` objects can still have their properties modified.'
            },
            {
              id: '1-3',
              question: 'What is hoisting in JavaScript?',
              hint: 'Think about how declarations are processed...',
              answer: 'Hoisting is JavaScript\'s default behavior of moving declarations to the top of their scope during compilation. This means you can use variables and functions before they are declared in the code.'
            }
          ]
        },
        {
          id: '2',
          name: 'React Concepts',
          description: 'Key concepts and patterns in React development',
          cards: [
            {
              id: '2-1',
              question: 'What is a React component?',
              hint: 'Think about reusable UI pieces...',
              answer: 'A React component is a reusable piece of UI that can contain its own logic and state. Components can be functional (using hooks) or class-based, and they return JSX to describe what should be rendered.'
            },
            {
              id: '2-2',
              question: 'What is the difference between props and state?',
              hint: 'One is passed down, the other is internal...',
              answer: 'Props are read-only data passed from parent to child components, while state is internal data that can change over time and triggers re-renders when updated. Props flow down, state is managed locally.'
            }
          ]
        }
      ];
      setTopics(sampleTopics);
      localStorage.setItem('flashcard-topics', JSON.stringify(sampleTopics));
    }
  }, []);

  // Save topics to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('flashcard-topics', JSON.stringify(topics));
  }, [topics]);

  const addTopic = (topic) => {
    const newTopic = {
      ...topic,
      id: Date.now().toString(),
      cards: []
    };
    setTopics([...topics, newTopic]);
  };

  const updateTopic = (topicId, updatedTopic) => {
    setTopics(topics.map(topic => 
      topic.id === topicId ? { ...topic, ...updatedTopic } : topic
    ));
  };

  const deleteTopic = (topicId) => {
    setTopics(topics.filter(topic => topic.id !== topicId));
    if (selectedTopic?.id === topicId) {
      setSelectedTopic(null);
      setIsDeckView(false);
    }
  };

  const addCardToTopic = (topicId, card) => {
    const newCard = {
      ...card,
      id: `${topicId}-${Date.now()}`
    };
    setTopics(topics.map(topic => 
      topic.id === topicId 
        ? { ...topic, cards: [...topic.cards, newCard] }
        : topic
    ));
  };

  const updateCard = (topicId, cardId, updatedCard) => {
    setTopics(topics.map(topic => 
      topic.id === topicId 
        ? {
            ...topic,
            cards: topic.cards.map(card => 
              card.id === cardId ? { ...card, ...updatedCard } : card
            )
          }
        : topic
    ));
  };

  const deleteCard = (topicId, cardId) => {
    setTopics(topics.map(topic => 
      topic.id === topicId 
        ? {
            ...topic,
            cards: topic.cards.filter(card => card.id !== cardId)
          }
        : topic
    ));
  };

  const startRevision = (topic) => {
    setSelectedTopic(topic);
    setIsDeckView(true);
  };

  const backToTopics = () => {
    setIsDeckView(false);
    setSelectedTopic(null);
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1 className="app-title">
          <span className="title-icon">üÉè</span>
          FlashCards
        </h1>
        <p className="app-subtitle">Learning Made Beautiful</p>
      </header>

      <main className="app-main">
        {!isDeckView ? (
          <TopicPanel
            topics={topics}
            onAddTopic={addTopic}
            onUpdateTopic={updateTopic}
            onDeleteTopic={deleteTopic}
            onAddCard={addCardToTopic}
            onUpdateCard={updateCard}
            onDeleteCard={deleteCard}
            onStartRevision={startRevision}
          />
        ) : (
          <FlashcardDeck
            topic={selectedTopic}
            onBack={backToTopics}
            onUpdateCard={updateCard}
            onDeleteCard={deleteCard}
          />
        )}
      </main>

      <footer className="app-footer">
        <p>&copy; 2024 FlashCards. Built with React & Framer Motion.</p>
      </footer>
    </div>
  );
}

export default App;
