import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { RotateCcw, Lightbulb } from 'lucide-react';
import './Flashcard.css';

const Flashcard = ({ card, showHint, onShowHint }) => {
  const [isFlipped, setIsFlipped] = useState(false);
  const [showHintLocal, setShowHintLocal] = useState(false);

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  const handleShowHint = () => {
    if (!showHint) {
      setShowHintLocal(!showHintLocal);
      onShowHint?.();
    }
  };

  const currentHint = showHint || showHintLocal;

  // Debug: Log the card data
  console.log('Flashcard data:', card);
  console.log('Card question:', card?.question);
  console.log('Card answer:', card?.answer);
  console.log('Question type:', typeof card?.question);
  console.log('Question length:', card?.question?.length);

  // If no card data, show a placeholder
  if (!card) {
    return (
      <div className="flashcard-wrapper">
        <div className="flashcard-placeholder">
          <h3>No card data available</h3>
          <p>Please check if the topic has any flashcards.</p>
        </div>
      </div>
    );
  }

  // Ensure we have valid question and answer
  const questionText = card.question || 'No question available';
  const answerText = card.answer || 'No answer available';

  // Force text to be a proper string and handle any encoding issues
  const sanitizedQuestion = String(questionText).replace(/[\u0000-\u001F\u007F-\u009F]/g, '');
  const sanitizedAnswer = String(answerText).replace(/[\u0000-\u001F\u007F-\u009F]/g, '');

  // Debug: Log the sanitized text
  console.log('Sanitized question:', sanitizedQuestion);
  console.log('Sanitized answer:', sanitizedAnswer);

  // If the text is still corrupted, use a fallback
  const displayQuestion = sanitizedQuestion.length > 0 ? sanitizedQuestion : 'Question not available';
  const displayAnswer = sanitizedAnswer.length > 0 ? sanitizedAnswer : 'Answer not available';

  return (
    <div className="flashcard-wrapper">
      <motion.div
        className="flashcard"
        animate={{ rotateY: isFlipped ? 180 : 0 }}
        transition={{ duration: 0.6, ease: "easeInOut" }}
        style={{ transformStyle: "preserve-3d" }}
      >
        {/* Front of the card */}
        <motion.div
          className="card-face card-front"
          style={{ backfaceVisibility: "hidden" }}
        >
          <div className="card-header">
            <div className="card-suit">♠</div>
            <div className="card-corner">Q</div>
          </div>

          <div className="card-content">
            <h3 className="card-question">
              {displayQuestion}
            </h3>

            {/* Debug info - remove this after fixing */}
            <div style={{ 
              fontSize: '0.8rem', 
              color: '#888', 
              marginBottom: '1rem',
              padding: '0.5rem',
              backgroundColor: 'rgba(255,255,255,0.1)',
              borderRadius: '4px'
            }}>
              Debug: "{displayQuestion}" (Length: {displayQuestion.length})
            </div>

            {/* Test display - remove after fixing */}
            <div style={{
              fontSize: '1rem',
              color: '#00ff00',
              backgroundColor: 'rgba(0,255,0,0.1)',
              padding: '0.5rem',
              borderRadius: '4px',
              marginBottom: '1rem',
              border: '1px solid #00ff00'
            }}>
              Test: {displayQuestion}
            </div>

            {card.hint && (
              <motion.button
                className="hint-button"
                onClick={handleShowHint}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <Lightbulb size={16} />
                Show Hint
              </motion.button>
            )}

            {currentHint && card.hint && (
              <AnimatePresence>
                <motion.div
                  className="hint-display"
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.3 }}
                >
                  <div className="hint-icon">💡</div>
                  <p className="hint-text">{card.hint}</p>
                </motion.div>
              </AnimatePresence>
            )}
          </div>

          <div className="card-footer">
            <motion.button
              className="flip-button"
              onClick={handleFlip}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <RotateCcw size={18} />
              Flip Card
            </motion.button>
          </div>
        </motion.div>

        {/* Back of the card */}
        <motion.div
          className="card-face card-back"
          style={{ backfaceVisibility: "hidden", transform: "rotateY(180deg)" }}
        >
          <div className="card-header">
            <div className="card-suit">♥</div>
            <div className="card-corner">A</div>
          </div>

          <div className="card-content">
            <h3 className="card-answer">Answer</h3>
            <div className="answer-content">
              {displayAnswer}
            </div>
          </div>

          <div className="card-footer">
            <motion.button
              className="flip-button"
              onClick={handleFlip}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <RotateCcw size={18} />
              Flip Back
            </motion.button>
          </div>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default Flashcard;
