import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { RotateCcw, Lightbulb } from 'lucide-react';
import './Flashcard.css';

const Flashcard = ({ card, showHint, onShowHint }) => {
  const [isFlipped, setIsFlipped] = useState(false);
  const [showHintLocal, setShowHintLocal] = useState(false);

  const handleFlip = () => {
    setIsFlipped(!isFlipped);
  };

  const handleShowHint = () => {
    if (!showHint) {
      setShowHintLocal(!showHintLocal);
      onShowHint?.();
    }
  };

  const currentHint = showHint || showHintLocal;

  // Debug: Log the card data
  console.log('Flashcard data:', card);
  console.log('Card question:', card?.question);
  console.log('Card answer:', card?.answer);

  // If no card data, show a placeholder
  if (!card) {
    return (
      <div className="flashcard-wrapper">
        <div className="flashcard-placeholder">
          <h3>No card data available</h3>
          <p>Please check if the topic has any flashcards.</p>
        </div>
      </div>
    );
  }

  // Simple version for debugging
  return (
    <div className="flashcard-wrapper">
      <div className="flashcard-simple">
        <div className="card-header">
          <div className="card-suit">â™ </div>
          <div className="card-corner">Q</div>
        </div>

        <div className="card-content">
          <h3 className="card-question">
            {card.question || 'No question available'}
          </h3>

          {card.hint && (
            <button
              className="hint-button"
              onClick={handleShowHint}
            >
              <Lightbulb size={16} />
              Show Hint
            </button>
          )}

          {currentHint && card.hint && (
            <div className="hint-display">
              <div className="hint-icon">ðŸ’¡</div>
              <p className="hint-text">{card.hint}</p>
            </div>
          )}
        </div>

        <div className="card-footer">
          <button
            className="flip-button"
            onClick={handleFlip}
          >
            <RotateCcw size={18} />
            Flip Card
          </button>
        </div>
      </div>
    </div>
  );
};

export default Flashcard;
